uniqUserByName:
query uniq_user_by_name($name: String, $offset: Int, $not: String) {
  assignment_unique_assignment_aggregate(
    where: {_or: [{Name: {_ilike: $name}}, {FamilyName: {_ilike: $name}}, {WorkEmail: {_ilike: $name}}, {WorkMobilePhone: {_ilike: $name}}, {FirstLastName: {_ilike: $name}}, {LastFirstName: {_ilike: $name}}], _not: {PersonFestCloudID: {_eq: $not}}}
  ) {
    aggregate {
      count
      __typename
    }
    __typename
  }
  assignment_unique_assignment(
    limit: 20
    offset: $offset
    where: {_or: [{Name: {_ilike: $name}}, {FamilyName: {_ilike: $name}}, {WorkEmail: {_ilike: $name}}, {WorkMobilePhone: {_ilike: $name}}, {FirstLastName: {_ilike: $name}}, {LastFirstName: {_ilike: $name}}], _not: {PersonFestCloudID: {_eq: $not}}}
  ) {
    Age
    Birthday
    FamilyName
    Gender
    MiddleName
    Name
    PersonFestCloudID
    PositionFestCloudID
    PositionName
    StartDate
    WorkEmail
    WorkMobilePhone
    WorkgroupFestCloudID
    WorkgroupName
    __typename
  }
}
